import React, { Component } from 'react';
import {
  Form,
  FormGroup,
  ControlLabel,
  FormControl,
  HelpBlock,
  Checkbox,
  Radio,
  FieldGroup,
  Grid,
  Row,
  Col,
  Button,
  OverlayTrigger,
  Popover,
  Modal,
} from 'react-bootstrap';
import './App.css';

class Form1 extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      value: ''
    };
  }

  getValidationState() {
    const length = this.state.value.length;
    if (length > 10) return 'success';
    else if (length > 5) return 'warning';
    else if (length > 0) return 'error';
    return null;
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }


  render() {
    return (

      <form>
      <FormGroup controlId="formBasicText" >
        <ControlLabel>First Name</ControlLabel>
        <FormControl
          type="text"
          placeholder="Enter First Name" />
      </FormGroup>
      <FormGroup controlId="formBasicText" >
        <ControlLabel>Last Name</ControlLabel>
        <FormControl
          type="text"
          placeholder="Enter Last Name" />
      </FormGroup>
      <FormGroup controlId="formBasicText" >
        <ControlLabel>EmailID</ControlLabel>
        <FormControl
          type="email"
          placeholder="Enter Email" />
      </FormGroup>

      <FormGroup>
      <Checkbox inline>1</Checkbox> 
      <Checkbox inline>2</Checkbox>
      <Checkbox inline>3</Checkbox>
    </FormGroup>
    <FormGroup>
    <Radio name="radioGroup" inline>Male</Radio>
    <Radio name="radioGroup" inline>Female</Radio>
    </FormGroup>
    <FormGroup controlId="formBasicText" >
        <ControlLabel>Enter Age</ControlLabel>
        <FormControl
        min="10"
        max="100"
          type="number"
          placeholder="Enter Age" />
      </FormGroup>    </form>
    );
  }
}
export default Form1;
